document.addEventListener('DOMContentLoaded', function () {
    // Smooth scroll animation for elements with the 'animate-on-scroll' class
    gsap.utils.toArray('.animate-on-scroll').forEach(element => {
        gsap.fromTo(element, 
            {
                opacity: 0, // Start with opacity zero (invisible)
                y: 50 // Start 50px below its original position
            }, 
            {   
                scrollTrigger: {
                    trigger: element,
                    start: "top 80%", // Trigger animation when the top of the element hits 80% of the viewport height
                    end: "bottom 20%", // End animation when the bottom of the element passes 20% of the viewport
                    scrub: true, // Smoothly link animation to scroll position
                    toggleActions: "play none none reverse", // Play animation on scroll and reverse it when scrolling back
                    once: false // Will animate every time it's scrolled into view
                },
                opacity: 1, // Animate to full opacity
                y: 0, // Move back to the original position
                duration: 1, // Duration of 1 second for the animation
                ease: 'power4.out'
            }
        );
    });
  
    // Animate the hero content on page load
    gsap.from('.hero-content', {
        duration: 1.5,
        y: 50,
        opacity: 0,
        ease: 'power2.out',
        delay: 0.5
    });
  
    // Add hover effect to navigation links with GSAP
    document.querySelectorAll('.nav-link').forEach(link => {
        link.addEventListener('mouseenter', () => {
            gsap.to(link, { color: 'var(--color-accent)', duration: 0.3 });
        });
  
        link.addEventListener('mouseleave', () => {
            gsap.to(link, { color: 'var(--color-white)', duration: 0.3 });
        });
    });
  });